  const [posts, setPosts] = useState(null);
  const location = useLocation();
  const fetchPosts = async () => {
    const { data } = await axios.get("http://localhost:3001/todos");
    setPosts(data);
  };
  return (
    <>
      Home sweet Home
      <hr />
      <div>
        {" "}
        {`현재페이지: ${location.pathname.slice(1)}`},,,,,, <hr />
        <Link to="/about">ABOUT으로 이동! </Link>
      </div>
    </>
  );
}

