  const location = useLocation();
  return (
    <div>
      Home sweet Home
      <div>
        {" "}
        {`현재페이지: ${location.pathname.slice(1)}`}
        <Link to="/about">ABOUT으로 이동! </Link>
      </div>
    </div>
  );
}

export default App;
